data.template:
pid /nginx.pid;
error_log /error.log;
worker_processes  1;
events { worker_connections  1024; }
http {
include /etc/nginx/mime.types;
server_names_hash_max_size 512;
server_names_hash_bucket_size 256;
client_max_body_size 64M;
index index.html;
log_format full '$remote_addr - $remote_user [$time_local] '
                '"$request" $status $body_bytes_sent '
                '"$http_referer" "$http_user_agent" $request_time';
access_log /access.log full;
error_log /error.log;
client_body_temp_path ;
fastcgi_temp_path ;

autoindex on;

gzip on;
gzip_types text/css text/plain application/xml application/json application/javascript;

map $http_x_forwarded_proto $real_scheme {
  default $http_x_forwarded_proto;
  ''      $scheme;
}

proxy_temp_path ;
proxy_read_timeout 600;
proxy_connect_timeout 600;
proxy_set_header X-Forwarded-Host $host;
proxy_set_header X-Forwarded-Proto $real_scheme;
proxy_set_header Host $host;

server {
    listen 8080 default;
    location /runbot/static/ {
      alias ;
      autoindex off;
      return 404;
      location /runbot/static/src { }
      location ~ /runbot/static/build/[^/]+/(logs|tests|coverage)/ {
          autoindex on;
          add_header 'Access-Control-Allow-Origin' '';
      }
    }
}





server {
    listen 8080;
    server_name ~^(-[a-z0-9_]+)?\.$;
    location / { proxy_pass http://127.0.0.1:; }
    location /longpolling { proxy_pass http://127.0.0.1:; }
    location /websocket {
      proxy_pass http://127.0.0.1:;
      proxy_set_header X-Forwarded-Host $host;
      proxy_set_header X-Forwarded-Proto $real_scheme;
      proxy_set_header Host $host;
      proxy_set_header Upgrade $http_upgrade;
      proxy_set_header Connection "Upgrade";
    }

}

server {
    listen 8080;
    server_name ~.+\.$;
    location / { return 404; }
}
}
